DROP TABLE IF EXISTS apparels;
CREATE TABLE IF NOT EXISTS apparels (
	id INT PRIMARY KEY UNIQUE,
	name	TEXT,
	description	TEXT,
	img_url	TEXT,
	category	TEXT,
	inventory	INT,
	price	DOUBLE
);
