INSERT INTO apparels VALUES(5,'Whitney Pullover',replace('<p><p><em>This is a demonstration store. You can purchase products like this from <a href=http://unitedbyblue.com/ target=_blank>United By Blue</a>.</em></p>\n<p><span style=line-height: 1.4;><span style=line-height: 1.4;>Buttons are fussy. Sometimes you just want to roll out of bed, put on the pull over and get to the days work. </span></span>Julie is 5''8 and wearing a size small.</p>\n<ul class=tabs-content>\n<li><span style=line-height: 1.4;>100% Wool, Heavy 4 gauge thickness</span></li>\n<li><span style=line-height: 1.4;>Handmade in Nepal.</span></li>\n</ul>\n<ul class=tabs-content></ul></p>','\n',chr(10)),'https://cdn.shopify.com/s/files/1/0803/6591/products/WhitneyPullover_Full_58e7b8d6-b939-4701-9e1d-9d853dff60ed.jpeg?v=1426786004','Sweaters',56,75.950000000000002843);
