INSERT INTO apparels VALUES(17,'Cydney Plaid',replace('<p><p><em>This is a demonstration store. You can purchase products like this from <a href=http://unitedbyblue.com/ target=_blank>United By Blue</a>.</em></p>\n<ul class=tabs-content>\n<li><span style=line-height: 1.4;>Super soft, super responsible - our wovens were made to last.</span></li>\n<li><span style=line-height: 1.4;>Short sleeve</span></li>\n<li><span style=line-height: 1.4;>Made of 100% Organic Cotton Plaid, 4 oz</span></li>\n<li><span style=line-height: 1.4;>Made in USA</span></li>\n</ul>\n<ul class=tabs-content></ul></p>','\n',chr(10)),'https://cdn.shopify.com/s/files/1/0803/6591/products/CydneyPlaid_Longsleeve_e72db08c-cd32-41eb-8251-d826cf0fc299.jpeg?v=1426786085','Shirts',79,8.9499999999999992894);
INSERT INTO apparels VALUES(18,'Red Wing Iron Ranger Boot',replace('<p><p><em>This is a demonstration store. You can purchase products like this from <a href=http://unitedbyblue.com/ target=_blank>United By Blue</a>.</em></p>\n<p><span style=line-height: 1.4;>The Mesabi Iron Range lies in northern Minnesota, a rugged and remote area known for its iron mines. The local residents who work these mines are proudly known as Iron Rangers, individuals with a sense of adventure and a determined personality. Originally designed to be worn in the iron mines, Iron Ranger work boots had to be as tough as the people who wore them in demanding conditions. Iron Ranger boots are built with a double layer of leaver over the toe to provide an extra measure of safety.</span></p>\n<ul class=tabs-content>\n<li><span style=line-height: 1.5;>6 inch, Amber Harness leather with Nitrile Cork sole.</span></li>\n</ul>\n<ul class=tabs-content></ul></p>','\n',chr(10)),'https://cdn.shopify.com/s/files/1/0803/6591/products/boot.jpg?v=1426709753','Footwear',74,67.950000000000002841);
INSERT INTO apparels VALUES(19,'Long Sleeve Swing Shirt',replace('<p><p><em>This is a demonstration store. You can purchase products like this from <a href=http://unitedbyblue.com/ target=_blank>United By Blue</a>.</em></p>\n<ul class=tabs-content>\n<li><span style=line-height: 1.4;>We updated our standard with a relaxed fit that stands up to constant wear.</span></li>\n<li><span style=line-height: 1.4;>100% Organic Cotton</span></li>\n<li><span style=line-height: 1.4;>Imported</span></li>\n<li>Relaxed Fit.</li>\n</ul>\n<p><span style=line-height: 1.5;>Julie is 5''8 and is wearing a size small.</span></p>\n<ul class=tabs-content></ul>\n<ul class=tabs-content></ul></p>','\n',chr(10)),'https://cdn.shopify.com/s/files/1/0803/6591/products/swing_deepwater_26dfd395-1a9b-4ff0-9f47-10331738fded.jpeg?v=1426786268','Shirts',4,158.94999999999998862);
