INSERT INTO fashion VALUES(988,'Long Sleeved Tee','<p>The Long Sleeved Tee is slim fitting and lightweight. Seasonless by nature, the highly versatile top is layer-able for all weather. Wide crew neckline. Colors available; White, Black, Beige. 100% Organic Cotton. Made in Peru. <em>Ashley is wearing an X-Small.</em></p>','https://cdn.shopify.com/s/files/1/0923/8036/products/2015-07-02_Ashley_18_50987_23882.jpeg?v=1437064187','arrivals, AW15, beige, black, feather, organic, organic by john patrick, Shot 7/2/15, tops, white, Woman',20,28.949999999999999288);
